pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "./interfaces/IBetManager.sol";
import "./interfaces/ILeague.sol";
import "./interfaces/ILeagueRegistry.sol";
import "./interfaces/IRegistry.sol";
import "./interfaces/IResolver.sol";
import "./interfaces/IVault.sol";
import "./libraries/BetLib.sol";
import "./libraries/SignatureLib.sol";

/**
* @title Bet Manger Contract
* @notice BetManager is the core contract responsible for bet validations and bet submissions
*/
contract BetManager is IBetManager, Ownable {

  using SafeMath for uint;
  using BetLib for BetLib.Bet;

  struct FillResults {
    uint backerTokenAmount;
    uint layerTokenAmount;
    uint backerFee;
    uint layerFee;
  }

  // The number of decimals of backerOdds
  uint constant ODDS_DECIMALS = 8;

  // Address of the Registry contract
  IRegistry registry;

  // Map of bet ids to bet struct  TODO: Ryan => make internal?
  mapping (bytes32 => BetLib.Bet) public bets;
  // Map of bet ids to the amount the bet that has been filled
  mapping (bytes32 => uint) public filled;
  // Evaluates to `true` if bet has been cancelled, `false` otherwise
  mapping (bytes32 => bool) public cancelled;

  // Map of bet ids to the amount that a layerd has filled
  mapping (bytes32 => mapping (address => uint)) public layerFilled; // bet Id => address => fill amount
  // Evaluates to `true` if bet has been claimed, `false` otherwise
  mapping (bytes32 => mapping (address => bool)) public claimed; // bet Id => address => claimed
  // Map of addresses and a list of bet ids that they have either backed/layed
  mapping (address => bytes32[]) public userBetIds; // user's address => array of bet Ids

  // Emit when a bet has been filled
  // TODO: Ryan => decide which parameters should be indexed
  // TODO: Ryan => decide if we need to emit all these params
  event LogBetAddressesFilled(
    bytes32 indexed _betHash,
    address _backerAddress,
    address _layerAddress,
    address _backerTokenAddress,
    address _layerTokenAddress,
    address _feeRecipientAddress,
    address _leagueAddress,
    address _resolverAddress
  );
  event LogBetValuesFilled(
    bytes32 indexed _betHash,
    uint _backerTokenStake,
    uint _layerTokenStake,
    uint _backerFee,
    uint _layerFee,
    uint _expirationTimeSeconds,
    uint _fixutedId,
    uint _backerOdds
  );
  event LogBetPayloadFilled(bytes32 indexed _betHash, bytes _betPayload);
  // Emit when a bet is cancelled
  event LogBetCancelled(bytes32 indexed _betHash);
  // Emit when a bet is claimed
  event LogBetClaimed(bytes32 indexed _betHash, address indexed _claimerAddress, uint8 indexed _result);

  /**
   * @notice Constructor
   * @param _registry address of Registry contract
   */
  constructor (address _registry) public {
    registry = IRegistry(_registry);
  }

  /**
   * @notice Fills a bet
   * @param _betAddresses Array of addresses that are associated with the bet [backerAddress, layerAddress, backerTokenAddress, layerTokenAddress, feeRecipientAddress, leagueAddress, resolverAddress]
   * @param _betValues Array of uint256 that as associated with the bet [backerTokenStake, backerFee, layerFee, expirationTimeSeconds, fixtureId, backerOdds]
   * @param _layerTokenFillAmount The number of tokens that they layer intends to bet
   * @param _salt Arbitrary number to ensure uniqueness of bet hash
   * @param _betPayload The payload of the bet
   * @param _signature ECDSA signature along with the mode (0 = Typed (EIP712), 1 = Geth, 2 = Trezor) {mode}{v}{r}{s}.
     // TODO: Ryan => implement fillOrKill functionality i.e. only accept bets if the entire amount is filled
   */
  function fillBet(
    address[7] _betAddresses,
    uint[6] _betValues,
    uint _layerTokenFillAmount,
    uint _salt,
    bytes _betPayload,
    bytes _signature
  ) external returns (bytes32) {

    // construct bet from the parameters provided
    BetLib.Bet memory bet = BetLib.createBet(_betAddresses, _betValues, _betPayload);
    bytes32 betHash = bet.hash(_salt);

    // calculate the amount that the layer is able to fill
    uint remaining = bet.backerTokenReturn(ODDS_DECIMALS).sub(filled[betHash]);
    uint layerTokenFillAmount = _layerTokenFillAmount < remaining ? _layerTokenFillAmount : remaining;

    // validate all the parameters of the bet
    validateBet(bet, _signature, betHash, layerTokenFillAmount);

    // calculate the partial fill results  TODO: only need layerTokenAmount and backerTokenAmount
    FillResults memory fillResults = calculatePartialFillResults(bet, layerTokenFillAmount);

    // validate the backer and layer
    validateBettor(bet.backerAddress, bet.backerTokenAddress, fillResults.backerTokenAmount);
    validateBettor(msg.sender, bet.layerTokenAddress, fillResults.layerTokenAmount);

    // increment the amount that the layer is filling
    layerFilled[betHash][msg.sender] += fillResults.layerTokenAmount;
    filled[betHash] += fillResults.backerTokenAmount;

    // escrow tokens to the bet manager
    IVault vault = IVault(registry.getAddress("Vault"));
    vault.transfer(bet.backerTokenAddress, bet.backerAddress, this, fillResults.backerTokenAmount);
    vault.transfer(bet.layerTokenAddress, msg.sender, this, fillResults.layerTokenAmount);

    // check if bet has been already filled
    if (filled[betHash] == 0) {
      bets[betHash] = bet;
      userBetIds[bet.backerAddress].push(betHash);
    }

    // add the bet id to the user
    userBetIds[msg.sender].push(betHash);

    emit LogBetAddressesFilled(
      betHash,
      bet.backerAddress,
      msg.sender,
      bet.backerTokenAddress,
      bet.layerTokenAddress,
      bet.feeRecipientAddress,
      bet.leagueAddress,
      bet.resolverAddress
    );

    emit LogBetValuesFilled(
      betHash,
      fillResults.backerTokenAmount,
      fillResults.layerTokenAmount,
      bet.backerFee,
      bet.layerFee,
      bet.expirationTimeSeconds,
      bet.fixtureId,
      bet.backerOdds
    );

    emit LogBetPayloadFilled(
      betHash,
      _betPayload
    );
  }

  /**
   * @notice Claims a bet, transfers tokens based on the result of the bet and transfers FAN token fees accordingly
   * @param _betHash Hash of all the bet parameters
   * @return Result of bet
   */
  function claimBet(bytes32 _betHash) external returns (uint8) {
    require(
      !claimed[_betHash][msg.sender],
      "Bet should not have been claimed yet"
    );
    require(
      bets[_betHash].backerAddress == msg.sender || layerFilled[_betHash][msg.sender] != 0,
      "Claimer must be the backer or one of the layers of the bet"
    );

    BetLib.Bet memory bet = bets[_betHash];
    ILeague league = ILeague(bet.leagueAddress);

    require(
      league.isFixtureResolved(bet.fixtureId, bet.resolverAddress) == 2,
      "Result is not available for resolver"
    );

    uint8 result = getResult(bet.resolverAddress, bet.leagueAddress, bet.fixtureId, bet.betPayload);
    IVault vault = IVault(registry.getAddress("Vault"));

    // Claimer is the backer
    if (msg.sender == bet.backerAddress) {

      uint backerAmount = filled[_betHash];
      uint winAmount = backerAmount.mul(bet.backerOdds).div(10 ** ODDS_DECIMALS);
      FillResults memory backerFillResults = calculatePartialFillResults(bet, winAmount);

      // Backer Wins
      if(result == 1) {
        vault.transfer(bet.backerTokenAddress, this, msg.sender, backerAmount);
        vault.transfer(bet.layerTokenAddress, this, msg.sender, winAmount);
        transferFee(msg.sender, bet.feeRecipientAddress, backerFillResults.backerFee, vault);
      }

      // Backer Half-Wins
      if(result == 2) {
        vault.transfer(bet.backerTokenAddress, this, msg.sender, backerAmount);
        vault.transfer(bet.layerTokenAddress, this, msg.sender, winAmount.div(2));
        transferFee(msg.sender, bet.feeRecipientAddress, backerFillResults.backerFee, vault);
      }

      // Backer Half-Loses
      if(result == 4) {
        vault.transfer(bet.backerTokenAddress, this, msg.sender, backerAmount.div(2));
      }

      // Backer Pushes
      if(result == 5) {
        vault.transfer(bet.backerTokenAddress, this, msg.sender, backerAmount);
      }
    }

    // Claimer is the layer
    if (layerFilled[_betHash][msg.sender] != 0) {

      uint layerAmount = layerFilled[_betHash][msg.sender];
      FillResults memory layerFillResults = calculatePartialFillResults(bet, layerAmount);

      // Layer Half-Loses
      if(result == 2) {
        vault.transfer(bet.layerTokenAddress, this, msg.sender, layerAmount.div(2));
      }

      // Layer Wins
      if(result == 3) {
        vault.transfer(bet.layerTokenAddress, this, msg.sender, layerAmount);
        vault.transfer(bet.backerTokenAddress, this, msg.sender, layerFillResults.backerTokenAmount);
        transferFee(msg.sender, bet.feeRecipientAddress, layerFillResults.layerFee, vault);
      }

      // Layer Half-Wins
      if(result == 4) { // Half Lose (Layer Half Win)
        vault.transfer(bet.layerTokenAddress, this, msg.sender, layerAmount);
        vault.transfer(bet.backerTokenAddress, this, msg.sender, layerFillResults.backerTokenAmount.div(2));
        transferFee(msg.sender, bet.feeRecipientAddress, layerFillResults.layerFee, vault);
      }

      // Layer Pushes
      if(result == 5) {
        vault.transfer(bet.layerTokenAddress, this, msg.sender, layerAmount);
      }
    }

    claimed[_betHash][msg.sender] = true;

    emit LogBetClaimed(_betHash, msg.sender, result);
    return result;
  }

  /**
   * @notice Cancels a bet which has been partially filled
   * @param _betHash Hash of all the bet parameters
   * @return Returns `true` if bet was successfully cancelled
   */
  function cancelBet(bytes32 _betHash) external returns (bool) {
    require(
      bets[_betHash].backerAddress == msg.sender,
      "Sender must be backer of bet"
    );
    require(
      filled[_betHash] > 0,
      "Bet must exist"
    );
    cancelled[_betHash] = true;

    emit LogBetCancelled(_betHash);

    return true;
  }

  /**
   * @notice Returns all the bet ids (hashes) for the provided user address
   * @param _user Address of the user
   */
  function getUserBetIds(address _user) external view returns (bytes32[]) {
    return userBetIds[_user];
  }

  /**
   * @notice Returns all the parameters associated with a bet hash
   * @param _betHash Hash of all the bet parameters
   * @return Array of addresses that are associated with the bet [backerAddress, layerAddress, backerTokenAddress, layerTokenAddress, feeRecipientAddress, leagueAddress, resolverAddress]
   * @return Array of uint256 that as associated with the bet [backerTokenStake, backerFee, layerFee, expirationTimeSeconds, fixtureId, backerOdds]
   * @return The payload of the bet
  */
  function getBetDetails(bytes32 _betHash) external view returns (address[7], uint[6], bytes) {
    BetLib.Bet memory bet =  bets[_betHash];
    return ([
        bet.backerAddress,
        bet.layerAddress,
        bet.backerTokenAddress,
        bet.layerTokenAddress,
        bet.feeRecipientAddress,
        bet.leagueAddress,
        bet.resolverAddress
      ], [
        bet.backerTokenStake,
        bet.backerFee,
        bet.layerFee,
        bet.expirationTimeSeconds,
        bet.fixtureId,
        bet.backerOdds
      ],
      bet.betPayload
    );
  }

  /**
   * @notice Transfer FAN Token fee
   * @param _feePayer Address of payer of the fee
   * @param _feeRecipient Address of recipient of the fee
   * @param _amount Number of Fan Tokens to transfer
   * @param _vault Address of vault contract
   */
  function transferFee(address _feePayer, address _feeRecipient, uint _amount, IVault _vault) internal {
    if(_amount > 0) {
      address fanToken = registry.getAddress("FanToken");
      _vault.transfer(fanToken, _feePayer, _feeRecipient, _amount);
    }
  }

  /**
   * @notice Validation of bet parameters
   * @param _bet Bet struct of all bet parameters
   * @param _signature ECDSA signature along with the mode (0 = Typed (EIP712), 1 = Geth, 2 = Trezor) {mode}{v}{r}{s}.
   * @param _betHash Hash of all bet parameters
   * @param _layerTokenFillAmount The number of tokens that they layer intends to bet
   */
  function validateBet(BetLib.Bet _bet, bytes _signature, bytes32 _betHash, uint _layerTokenFillAmount) internal view {
    require(
      filled[_betHash] < _bet.backerTokenStake,
      "Bet should not bet fully filled"
    );
    require(
      _bet.layerAddress == 0x0 || _bet.layerAddress == msg.sender,
      "Sender must be equal to layer if layerAddress exists"
    );
    require(
      _bet.expirationTimeSeconds > now,
      "Expiration time must be greater than now"
    );
    require(
      ILeagueRegistry(registry.getAddress("LeagueRegistry")).isLeagueRegistered(_bet.leagueAddress),
      "League must be registered"
    );
    require(
      ILeague(_bet.leagueAddress).isResolverRegistered(_bet.resolverAddress),
      "Resolver must be registered for league"
    );
    require(
      ILeague(_bet.leagueAddress).isFixtureScheduled(_bet.fixtureId),
      "Fixture must be scheduled"
    );
    require(
      !cancelled[_betHash],
      "Bet should not be cancelled"
    );
    require(
      _layerTokenFillAmount != 0,
      "Layer token fill amount must be greater than 0"
    );
    require(
      _bet.backerAddress != msg.sender,
      "Sender address cannot equal layerAddress"
    );

    // check whether the bet has not been partial filled yet
    if(filled[_betHash] == 0) {
      require(
        _bet.backerTokenStake != 0,
        "Backer token stake must be greater than 0"
      );
      require(
        _bet.backerOdds != 0,
        "Backer odds must be greater than 0"
      );
      require(
        _bet.backerAddress != _bet.layerAddress,
        "Backer address cannot equal layerAddress"
      );
      require(
        validateBetParamsPayload(_bet.resolverAddress, _bet.betPayload),
        "Bet payload must be must be valid for resolver"
      );
      require(
        SignatureLib.isValidSignature(_betHash, _bet.backerAddress, _signature),
        "Signature is not valid"
      );
    }
  }

  /**
   * @notice Validation of bettor (backer or layer)
   * @param _bettor Address of backer or layer
   * @param _token Address of token that is being bet
   * @param _tokenStakeAmount Amount of tokens that the bettor is staking
   */
  function validateBettor(address _bettor, address _token, uint _tokenStakeAmount) internal view {
    IVault vault = IVault(registry.getAddress("Vault"));

    require(
      vault.isApproved(_bettor, this),
      "Bettor has not approved Bet Manager to access their balance"
    );
    require(
      vault.balanceOf(_token, _bettor) >= _tokenStakeAmount,
      "Bettor does not have enough tokens to cover the bet"
    );
  }

  /**
   * @notice Calculates the partial amounts for the backer stake, backer fee and layer fee based on the layer token fill amount
   * @param _bet Struct of bet parameters
   * @param _layerTokenFillAmount The number of tokens that they layer intends to bet
   * @return Struct with partial amounts for the backer fee, layer fee and layer fill amount
   */
  function calculatePartialFillResults(BetLib.Bet memory _bet, uint256 _layerTokenFillAmount) internal pure returns (FillResults memory fillResults) {
    fillResults.layerTokenAmount = _layerTokenFillAmount;
    fillResults.backerTokenAmount = getPartialAmount(
      _layerTokenFillAmount,
      _bet.backerTokenReturn(ODDS_DECIMALS),
      _bet.backerTokenStake
    );
    fillResults.backerFee = getPartialAmount(
      _layerTokenFillAmount,
      _bet.backerTokenReturn(ODDS_DECIMALS),
      _bet.backerFee
    );
    fillResults.layerFee = getPartialAmount(
      _layerTokenFillAmount,
      _bet.backerTokenReturn(ODDS_DECIMALS),
      _bet.layerFee
    );

    return fillResults;
  }

  /**
   * @notice Calculates the partial amount
   * @param _numerator Numerator
   * @param _denominator Denominator
   * @param _target Value to calculate partial of
   * @return Partial value of target
   */
  function getPartialAmount(uint _numerator, uint _denominator, uint _target) internal pure returns (uint256 partialAmount) {
    partialAmount = _numerator.mul(_target).div(_denominator);
    return partialAmount;
  }

  /**
   * @notice Validate bet params payload
   * @param _resolver Address of resolver
   * @param _betPayload Encoded bet data that can be decoded by the resolver
   * @return Returns `true` if the the bet payload is valid, `false` otherwise
   */
  function validateBetParamsPayload(address _resolver, bytes _betPayload) internal returns (bool) {
    // TODO: Ryan => implement resolvers
    return true;
  }

  /**
   * @notice Returns the result of the bet
   * @param _resolver Address of the resolver
   * @param _league Address of the league
   * @param _fixtureId Id of the fixture
   * @param _betPayload Encoded bet data that can be decoded by the resolver
   */
  function getResult(address _resolver, address _league, uint _fixtureId, bytes _betPayload) internal returns (uint8) {
    // TODO: Ryan => implement resolvers, code below is for testing
    uint x;
    assembly {
      x := mload(add(_betPayload, add(0x20, 0)))
    }
    return uint8(x);
  }
}

