pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "./interfaces/IResolverRegistry.sol";
import "./interfaces/ILeagueRegistry.sol";
import "./interfaces/IResolver.sol";
import "./interfaces/IRegistry.sol";


/**
* @title Resolver Registry Contract
* @notice Anyone can register Resolvers, but only those "verified" by FansUnite will be available for anyone to use
*/
contract ResolverRegistry is Ownable, IResolverRegistry {

  IRegistry public registry;

  mapping (address => bool) public registered;
  mapping (address => bool) public verified;
  mapping (address => bool) public active;
  address[] public resolvers;

  event LogResolverUsed(address indexed _resolver, address indexed _securityToken);
  event LogResolverRegistered(address indexed _resolver, address indexed _owner);
  event LogResolverVerified(address indexed _resolver, bool _verified);
  event LogResolverStatusChanged(address indexed _resolver, bool _status);

  modifier onlyRegistered(address _resolver) {
    require(registered[_resolver], "Resolver should be registered");
    _;
  }

  constructor (address _registry) public {
    registry = IRegistry(_registry);
  }

  function useResolver(address _resolver) onlyRegistered(_resolver) external {
    if(ILeagueRegistry(registry.getAddress("LeagueRegistry")).isLeagueRegistered(msg.sender)) {
      emit LogResolverUsed(_resolver, msg.sender);
    }
  }

  function registerResolver(address _resolver) external returns(bool) {
    require(!registered[_resolver], "Resolver should not be registered");

    IResolver resolver = IResolver(_resolver);

    registered[_resolver] = true;
    active[_resolver] = true;
    resolvers.push(_resolver);

    emit LogResolverRegistered(_resolver, resolver.owner());
    return true;
  }

  function verifyResolver(address _resolver, bool _verified) external onlyOwner onlyRegistered(_resolver) returns(bool) {
    verified[_resolver] = _verified;
    emit LogResolverVerified(_resolver, _verified);
    return true;
  }

  function changeResolverStatus(address _resolver, bool _status) external onlyOwner onlyRegistered(_resolver) returns(bool) {
    active[_resolver] = _status;
    emit LogResolverStatusChanged(_resolver, _status);
    return true;
  }

}
